mapper.select:
SELECT

FROM game_users a

WHERE a.id = #{id}

SELECT

FROM game_users a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}

		AND a.phone_num = #{phoneNum}



		AND a.realname LIKE
		'%'||#{realName}||'%'
		'%'+#{realName}+'%'
		concat('%',#{realName},'%')




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT

FROM game_users a


	a.del_flag = #{DEL_FLAG_NORMAL}
	${dataScope}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE game_users SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
